import React from 'react';

const Header = ({ weekDate }) => {
  const [ week, year ] = weekDate.split('-');
  return (
    <div className='header'>
      <h1 className='f1 fl w-50 pa2 tc title ml3'>HBC Coffee Week</h1>
      <h1 className='f1 fl w-10 pa2 tc ml3' id='divider'>|</h1>
      <h1 className='f1 fl w-50 pa2 tc pr7' id='weekIndicator'>Week {week} {year}</h1>
    </div>
  );
}

export default Header;
