import React from 'react';

const Toolbar = ({ searchfield, searchChange }) => {
  return (
    <div id="toolbar">
      <div className="pa2 flex flex-row">
        <h3>Show filter:</h3>
        <form class="pa2">
          <fieldset>
            <legend class="">Favorite Movies</legend>
            <div class="">
              <input class="mr2" type="checkbox" id="spacejam" value="spacejam"/>
              <label for="spacejam" class="lh-copy">Space Jam</label>
            </div>
          </fieldset>
        </form>
        <input 
          className="pa3 ba b--green bg-lightest-blue"
          type="search" 
          placeholder="search users" 
          onChange={searchChange}
      />
      </div>

    </div>
  );
}

export default Toolbar;
