import React, { Component } from 'react';
import CardList from '../components/CardList';
import Header from '../components/Header';
import Toolbar from '../components/Toolbar';
import Scroll from '../components/Scroll';
import './App.sass';

class App extends Component {
  constructor() {
    super();
    this.state = {
      response: '',
      searchField: '',
      weekDate: ''
    }
  }

  componentDidMount() {
    this.fetchCoffeeList()
      .then(res => this.setState({ response: res.coffeeUserList }))
      .catch(err => console.log(err));
    this.fetchWeekDate()
      .then(res => this.setState({ weekDate: res.weekDate }))
      .catch(err => console.log(err));
  }

  fetchCoffeeList = async () => {
    const response = await fetch('/api/fetchCoffeeList?department=engineering&location=dub');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  fetchWeekDate = async () => {
    const response = await fetch('/api/fetchWeekDate');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  }

  filterUser = () => {
    const { searchField, response } = this.state;
    let filteredUsers = [];
    let userPair;

    for (let i=0; i<response.length; i++) {
      userPair = response[i].filter(user => {
        return (user.name.first.toLowerCase().includes(searchField.toLowerCase())) 
          || (user.name.last.toLowerCase().includes(searchField.toLowerCase()));
      });
      if (userPair.length !== 0) {
        filteredUsers.push(response[i]);
      }
    }
    return filteredUsers;
  }

  onSearchChange = (event) => {
    this.setState({ searchField: event.target.value });
  }

  render() {
    const { response, weekDate } = this.state;
    const filteredUsers = this.filterUser();
    // console.log('RES:', response);

    return (!response.length ? 
      <h1>Loading</h1>
      :
      <div className='tc'>
        <Header weekDate={weekDate}></Header>
        <Toolbar searchChange={this.onSearchChange} checkboxChange={this.onCheckboxChange}/>
        <Scroll>
          <CardList hbcUsers={filteredUsers}></CardList>
        </Scroll>
      </div>
    )
  }
}

export default App;
