import React, { Component } from 'react';
import CardList from '../components/CardList';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      hbcUsers: [],
      response: '',
    }
  }

  componentDidMount() {
    fetch('https://hbc-frontend-challenge.hbccommon.private.hbc.com/coffee-week/users?department=engineering&location=dub')
        .then( response => response.json())
        .then( users => this.setState({ hbcUsers: users.users }));
    this.callApi()
      .then(res => this.setState({ response: res.coffeeUserList }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/fetchCoffeeList');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  render() {
    const { hbcUsers, response } = this.state;
    console.log(response);

    return (!hbcUsers.length ? 
      <h1>Loading</h1>
      :
      <div className='tc'>
        <h1 className='f1'>HBC Coffee Week</h1>
        <CardList hbcUsers={hbcUsers}></CardList>
      </div>
    )
  }
}

export default App;
