import React from 'react';

const CardSingle = ({ hbcUser }) => {
  console.log(hbcUser);
  return (
    <div className="bg-light-red grow w-50 fl h-100 pa3">
        <img className="roboHashImage" alt="hbcUsers" src={`https://robohash.org/${hbcUsers.guid}?200x200`}/>
        <div className="userCard">
          <h2>{hbcUsers.name.first + " " + hbcUsers.name.last}</h2>
          <p className="motto">{hbcUsers.motto}</p>
          <p className="email">{hbcUsers.email}</p>
          <p className="location">{hbcUsers.location}</p>
          <p className="department">{hbcUsers.department}</p>
        </div>
      </div>
  )
}

export default CardSingle;
