import React from 'react';

const Card = ({ giver, receiver }) => {
  giver.location = this.prettifyLocationName(giver.location);
  receiver.location = this.prettifyLocationName(receiver.location);

    return (
        <div>
          <div className="grow w-50 fl h-100 giverCard">
            <img className="roboHashImage" alt="hbcUsers" src={`https://robohash.org/${giver.guid}?200x200`}/>
            <div className="userCard">
              <h2>{giver.name.first + " " + giver.name.last}</h2>
              <p className="motto">{giver.motto}</p>
              <p className="email">{giver.email}</p>
              <p className="location department">{giver.location} - {giver.department}</p>
            </div>
            <div className="giverLabel">
              Giver
            </div>
          </div>
          <div className="grow w-50 fl h-100 receiverCard">
            <img className="roboHashImage" alt="hbcUsers" src={`https://robohash.org/${receiver.guid}?200x200`}/>
            <div className="userCard">
              <h2>{receiver.name.first + " " + receiver.name.last}</h2>
              <p className="motto">{receiver.motto}</p>
              <p className="email">{receiver.email}</p>
              <p className="location department">{receiver.location} - {receiver.department}</p>
            </div>
            <div className="receiverLabel">
              Receiver
            </div>
          </div>
        </div>
    )
}

function prettifyLocationName() {
  let prettify;
  switch (location) {
    case "dub":
      prettify = "Dublin";
      break;
    case "ny":
      prettify = "New York";
      break;
  }
  return prettify;
}

export default Card;
