import React, { Component } from 'react';
import CardList from '../components/CardList';
import Header from '../components/Header';
import Toolbar from '../components/Toolbar';
import Scroll from '../components/Scroll';
import './App.sass';

class App extends Component {
  constructor() {
    super();
    this.state = {
      response: '',
      searchField: '',
      weekDate: ''
    }
  }

  componentDidMount() {
    this.fetchCoffeeList()
      .then(res => this.setState({ response: res.coffeeUserList }))
      .catch(err => console.log(err));
    this.fetchWeekDate()
      .then(res => this.setState({ weekDate: res.weekDate }))
      .catch(err => console.log(err));
  }

  fetchCoffeeList = async () => {
    const response = await fetch('/api/fetchCoffeeList?department=engineering&location=dub');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  fetchWeekDate = async () => {
    const response = await fetch('/api/fetchWeekDate');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  }

  onSearchChange = (event) => {
    this.setState({ searchField: event.target.value });
  }

  render() {
    const { searchField, response, weekDate } = this.state;
    // const filteredUsers = response.filter(response => {
        // return robot.name.toLowerCase().includes(searchfield.toLowerCase());
    //     console.log(response);
    // });
    // console.log('WD:', weekDate);
    console.log('RES:', response);
    let filteredUsers = [];
    filteredUsers.push(response.filter(item => {
      // return (item.name.first.toLowerCase().includes(searchField.toLowerCase())) 
      //   || (item.name.last.toLowerCase().includes(searchField.toLowerCase()));
      console.log("TEST:", item);
    }));
    console.log(filteredUsers);
    // response.map((pairing, i) => {
    //   return (
    //     // <Card giver={pairing[0]} key={i} receiver={pairing[1]}/>
    //     console.log(pairing);
    //   )
    // })
    // console.log('Search Field:', searchField);

    return (!response.length ? 
      <h1>Loading</h1>
      :
      <div className='tc'>
        <Header weekDate={weekDate}></Header>
        <Toolbar searchChange={this.onSearchChange}/>
        {/* <Scroll> */}
        <CardList hbcUsers={filteredUsers}></CardList>
        {/* </Scroll> */}
      </div>
    )
  }
}

export default App;
